<template>
  <div class="footer flex">
    <div v-for="(item,index) in navList" :key="index+''" class="flex-1 flex-center-center" @click="navClick(item,index)">
      <div class="nav flex-column-center">
        <div class="footer-icon">
          <img v-if="index == navIndex" :src="item.iconActive" class="animate__rubberBand">
          <img v-else :src="item.icon">
        </div>
        <div>{{ item.name }}</div>
      </div>
    </div>
  </div>
</template>
<script>

import { mapGetters } from 'vuex'
export default {
  name: 'Nav',
  computed: {
    ...mapGetters([
      'navIndex'
    ])
  },
  data() {
    return {
      navList: [
        {
          name: '首页',
          path: '/',
          icon: 'data:image/png;base64,',
          iconActive: 'data:image/png;base64,'
        },
        {
          name: '优惠',
          path: '/activity',
          icon: 'data:image/png;base64,',
          iconActive: 'data:image/png;base64,'
        },
        {
          name: '推广',
          path: '/promote',
          icon: 'data:image/png;base64,',
          iconActive: 'data:image/png;base64,'
        },
        {
          name: '我的',
          path: '/mine',
          icon: 'data:image/png;base64,',
          iconActive: 'data:image/png;base64,'
        }
      ]

    }
  },
  methods: {
    navClick(item, index) {
      this.$store.dispatch('app/setNavIndex', index)
      this.$router.push({ path: item.path })
    }
  }
}
</script>
<style lang="scss" scoped>

.animate__rubberBand {
    animation-name: rubberBand;
    animation-fill-mode: both;
    animation-duration: .7s;
}

.footer {
  min-height: 3.4375rem;
  font-size: .8125rem;
  background: #fff;
  box-shadow: 0 -0.125rem 1.5625rem rgb(0 0 0 / 8%);
  border-radius: 1.25rem 1.25rem 0 0;
  border-width: 0 0;
  .footer-icon {
    width: 1.375rem;
    height: 1.375rem;
    margin-bottom: 0.3125rem;
  }
}

</style>
