<script>
import { array } from 'yargs'

export default {
  name: 'Dashboard',
  metaInfo: {
    title: '页面标题',
    meta: [
      { name: 'viewport', content: '页width=device-width,initial-scale=1面描述' },
      { name: 'viewport', content: 'width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no' }
    ]
  },
  data() {
    return {
      isShowIntro: false,
      isShowDetail: false,
      list: [],
      sorted:[]
    }
  },
  created() {
    for(let i=0; i<10; i++) {
      this.list.push({code:i,selected:false})
    }
    console.log(this.list)
  },
  methods: {
    handleSelectCode(item) {
      item.selected = !item.selected

      if(item.selected && !this.sorted.includes(item.code)) {
        this.sorted.push(item.code)
      }

      if(!item.selected && this.sorted.includes(item.code)) {
        this.sorted.map((val,i)=>{
          if(val === item.code) {
            this.sorted.splice(i,1)
          }
        })
      }

      if(this.sorted.length > 7) {
        let first = this.sorted.shift()
        this.list.map(m=>{
          if(m.code == first) {
            m.selected = false
          }
        })
      }

    }
  }
}
</script>

